from rest_framework.pagination import PageNumberPaginationfrom rest_framework.response import Responseclass Pagination(PageNumberPagination):    page_size = 4    page_size_query_param = 'size'    page_query_param = "page"    max_page_size = 1000    # 自定义    def get_paginated_response(self, data):        return Response({            'count': self.page.paginator.count,            'results': data        })